import React, { useState } from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');

  // Style objects
  const styles = {
    container: {
      height: '100vh',
      padding: 0
    },
    sidebar: {
      backgroundColor: '#f8f9fa',
      padding: '20px',
      height: '100vh',
      borderRight: '1px solid #dee2e6'
    },
    chatArea: {
      display: 'flex',
      flexDirection: 'column',
      height: '100vh'
    },
    messageContainer: {
      flex: 1,
      padding: '20px',
      overflowY: 'auto',
      backgroundColor: '#ffffff'
    },
    message: {
      marginBottom: '10px',
      padding: '10px',
      borderRadius: '5px',
      maxWidth: '70%'
    },
    userMessage: {
      backgroundColor: '#007bff',
      color: 'white',
      marginLeft: 'auto'
    },
    botMessage: {
      backgroundColor: '#e9ecef',
      marginRight: 'auto'
    },
    inputArea: {
      padding: '15px',
      backgroundColor: '#f8f9fa',
      borderTop: '1px solid #dee2e6',
      display: 'flex',
      gap: '10px'
    },
    textarea: {
      flex: 1,
      resize: 'none'
    }
  };

  const handleSend = () => {
    if (inputText.trim()) {
      setMessages([...messages, { text: inputText, sender: 'user' }]);
      setInputText('');
      // TODO: Add API call for chatbot response
    }
  };

  const handleDocumentUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      // TODO: Process document
    }
  };

  return (
    <Container fluid style={styles.container}>
      <Row>
        {/* Sidebar for Document Input */}
        <Col md={3} style={styles.sidebar}>
          <h4>Document Input</h4>
          <Form>
            <Form.Group controlId="formFile" className="mb-3">
              <Form.Label>Upload Document</Form.Label>
              <Form.Control type="file" onChange={handleDocumentUpload} />
            </Form.Group>
          </Form>
        </Col>

        {/* Main Chat Area */}
        <Col md={9} style={styles.chatArea}>
          <div style={styles.messageContainer}>
            {messages.map((msg, index) => (
              <div 
                key={index} 
                style={{
                  ...styles.message,
                  ...(msg.sender === 'user' ? styles.userMessage : styles.botMessage)
                }}
              >
                {msg.text}
              </div>
            ))}
          </div>

          <div style={styles.inputArea}>
            <Form.Control
              as="textarea"
              rows={2}
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              placeholder="Type your message..."
              style={styles.textarea}
            />
            <Button variant="primary" onClick={handleSend}>
              Send
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Chat;
