import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import Navbar from '../components/navbar';
import Footer from '../components/footer';

const About = () => {
  return (
    <Container fluid className="min-vh-100 d-flex flex-column" style={{ backgroundColor: "#000000", color: "#FFFFFF", fontFamily: "Rasa, sans-serif" }}>
      <Navbar username="JohnDoe" />
      <Row className="flex-grow-1 align-items-center justify-content-center">
        <Col className="text-center">
          <h1 style={{ color: "#2BC6D1", fontSize: "4rem", fontWeight: "bold" }}>About Evalumate</h1>
          <p style={{ fontSize: "1.5rem", marginTop: "20px" }}>
            Evalumate is a powerful tool designed to help you manage your notes and ideas efficiently. 
            With features like easy organization, quick access, and a user-friendly interface, 
            you can focus on what matters most.
          </p>
          <p style={{ fontSize: "1.5rem" }}>
            Whether you're a student, professional, or just someone who loves to jot down thoughts, 
            Evalumate is here to enhance your productivity.
          </p>
        </Col>
      </Row>
      <Footer />
    </Container>
  );
};

export default About;
