import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col } from "react-bootstrap";
import { motion } from "framer-motion";
import Particles from "../components/particles.js";
import GradientText from "../components/gradientText.js";
import Click from "../components/click.js";


function Home() {
  return (
    <Container fluid className="vh-100 d-flex flex-column" style={{ backgroundColor: "#000000", color: "#FFFFFF", fontFamily: "Rasa, sans-serif" }}>
        <Click
            sparkColor='#fff'
            sparkSize={10}
            sparkRadius={15}
            sparkCount={8}
            duration={400}
            />
      <Row className="flex-grow-1">
        <Col className="p-4 d-flex flex-column h-100 animate__animated animate__fadeInRight" style={{ position: "relative" }}>
          <div style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", zIndex: 0 }}>
            <Particles 
              particleCount={900}
              particleSpread={2}
              speed={0.1}
              particleColors={["silver"]}
              moveParticlesOnHover={true}
              particleHoverFactor={1}
              alphaParticles={true}
              particleBaseSize={50}
              sizeRandomness={0.5}
              cameraDistance={15}
              disableRotation={false}
            />
          </div>

          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-center"
            style={{ 
              position: "relative",
              zIndex: 1,
              marginTop: "20vh"
            }}
          >
            {/* <h1 style={{ color: "#2BC6D1", fontFamily: "Zen Dots, sans-serif", fontSize: "4rem" }}>
              Welcome
            </h1> */}
            <h1 style={{ color: "#2BC6D1", fontSize: "6rem", fontWeight: "bolder", textShadow: "0px 0px 10px rgba(0, 0,0, 0.5)",}}>
            <GradientText
            colors={["#2BC6D1", "#28007B", "#2BC6D1", "#28007B", "#2BC6D1"]}
            animationSpeed={9}
            showBorder={false}
            className="custom-class"
            >

            Welcome
            </GradientText>
            </h1>
            <p style={{ 
              color: "#FFFFFF", 
              fontSize: "1.5rem",
              marginTop: "2rem",
              maxWidth: "800px",
              marginLeft: "auto",
              marginRight: "auto"
            }}>
              Experience the future of interactive communication with our cutting-edge platform.
            </p>
          </motion.div>
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
