import React, { useState, useRef, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Form, InputGroup, Button, Alert } from "react-bootstrap";
import { Send, ChevronLeft, ChevronRight } from "react-bootstrap-icons";
import { motion } from "framer-motion";
import Particles from "../components/particles";
import GradientText from "../components/gradientText";
import Navbar from "../components/navbar";
import Footer from "../components/footer";

function Chat() {
  const [files, setFiles] = useState([]);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [uploadError, setUploadError] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const chatRef = useRef(null);

  const validFileTypes = ['application/pdf', 'text/plain', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
  const maxFileSize = 10 * 1024 * 1024;

  const handleFileUpload = async (event) => {
    const uploadedFiles = Array.from(event.target.files);
    setUploadError(null);
    setIsUploading(true);

    const validFiles = uploadedFiles.filter(file => validFileTypes.includes(file.type) && file.size <= maxFileSize);
    if (validFiles.length === 0) {
      setUploadError("Invalid files detected. Please upload only PDF, TXT, or DOC files under 10MB.");
      setIsUploading(false);
      return;
    }

    const formData = new FormData();
    validFiles.forEach(file => formData.append("files", file));

    try {
      const response = await fetch("http://127.0.0.1:8000/documents/uploads", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) throw new Error("Failed to upload files");

      setFiles(prevFiles => [...prevFiles, ...validFiles]);
    } catch (error) {
      setUploadError("Error uploading files. Please try again.");
    } finally {
      setIsUploading(false);
    }
  };

  const handleSendMessage = async () => {
    if (input.trim()) {
      const userMessage = { sender: "User", text: input, timestamp: new Date().toLocaleTimeString() };
      setMessages(prev => [...prev, userMessage]);
      setInput("");

      try {
        const response = await fetch("http://127.0.0.1:8000/documents/search", {
          method: "GET",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ query: input }),
        });

        if (!response.ok) throw new Error("Failed to fetch response");

        const data = await response.json();
        setMessages(prev => [...prev, { sender: "Bot", text: data.response, timestamp: new Date().toLocaleTimeString() }]);
      } catch (error) {
        setMessages(prev => [...prev, { sender: "Bot", text: "Error retrieving response. Please try again.", timestamp: new Date().toLocaleTimeString() }]);
      }
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === "Enter" && !event.shiftKey) {
      event.preventDefault();
      handleSendMessage();
    }
  };

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <Container fluid className="min-vh-100 d-flex flex-column" style={{ backgroundColor: "#000000", color: "#FFFFFF", fontFamily: "Rasa, sans-serif" }}>
      <Navbar username="JohnDoe" />
      <Row className="flex-grow-1" style={{ position: 'relative' }}>
        <Particles />
        {sidebarOpen && (
          <Col md={3} className="p-3 d-flex flex-column" style={{ backgroundColor: "rgba(26, 26, 26, 0.9)", backdropFilter: "blur(5px)", zIndex: 1 }}>
            <h4 style={{ color: "#2BC6D1", fontWeight: "bold" }}>Notebook</h4>
            <hr style={{ borderColor: "#2BC6D1" }} />
            <Form.Group>
              <Form.Label style={{ color: "#2BC6D1" }}>Upload Documents</Form.Label>
              <Form.Control type="file" multiple onChange={handleFileUpload} disabled={isUploading} />
              {isUploading && <div style={{ color: "#2BC6D1" }}>Uploading files...</div>}
              {uploadError && <Alert variant="danger" className="mt-2">{uploadError}</Alert>}
            </Form.Group>
            <ul className="mt-3" style={{ listStyle: "none" }}>
              {files.length > 0 ? files.map((file, index) => (
                <li key={index} style={{ color: "#2BC6D1" }}>{file.name}</li>
              )) : <li style={{ color: "#888" }}>No documents uploaded</li>}
            </ul>
          </Col>
        )}
        <Col md={sidebarOpen ? 9 : 12} className="p-4 d-flex flex-column">
          <h3 style={{ color: "#2BC6D1", fontWeight: "bold" }}>Evalumate</h3>
          <div ref={chatRef} className="flex-grow-1 overflow-auto p-4 rounded" style={{ backgroundColor: "rgba(26, 26, 26, 0.9)", backdropFilter: "blur(5px)", borderRadius: "20px" }}>
            {messages.map((msg, index) => (
              <motion.div key={index} initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.3 }}>
                <div style={{ maxWidth: "80%", padding: "14px 20px", borderRadius: "25px", color: msg.sender === "User" ? "#fff" : "#000", background: msg.sender === "User" ? "#2BC6D1" : "#f1f1f1" }}>
                  {msg.text}
                  <div style={{ fontSize: "12px", marginTop: "8px", textAlign: "right", opacity: 0.7 }}>{msg.timestamp}</div>
                </div>
              </motion.div>
            ))}
          </div>
          <InputGroup>
            <Form.Control as="textarea" rows={1} value={input} onChange={(e) => setInput(e.target.value)} onKeyDown={handleKeyPress} placeholder="Type a message..." />
            <Button onClick={handleSendMessage}><Send size={20} /></Button>
          </InputGroup>
        </Col>
      </Row>
      <Footer />
    </Container>
  );
}

export default Chat;
