import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Card } from "react-bootstrap";
import { motion } from "framer-motion";
import Particles from "../components/particles.js";
import GradientText from "../components/gradientText.js";
import Click from "../components/click.js";

function Home() {
  const [notebooks, setNotebooks] = useState([
    { title: "Notebook 1", description: "Explore your first notebook", link: "/notebook1" },
    { title: "Notebook 2", description: "Dive into your second notebook", link: "/notebook2" },
    { title: "Notebook 3", description: "Organize your ideas here", link: "/notebook3" }
  ]);

  return (
    <Container fluid className="vh-100 d-flex flex-column" style={{ backgroundColor: "#000000", color: "#FFFFFF", fontFamily: "Rasa, sans-serif" }}>
      <Click
        sparkColor='#fff'
        sparkSize={10}
        sparkRadius={15}
        sparkCount={8}
        duration={400}
      />
      <Row className="flex-grow-1">
        <Col className="p-4 d-flex flex-column h-100 animate__animated animate__fadeInRight" style={{ position: "relative" }}>
          <div style={{ position: "absolute", top: 0, left: 0, width: "100%", height: "100%", zIndex: 0 }}>
            <Particles 
              particleCount={900}
              particleSpread={2}
              speed={0.1}
              particleColors={["silver"]}
              moveParticlesOnHover={true}
              particleHoverFactor={1.2}
              alphaParticles={true}
              particleBaseSize={50}
              sizeRandomness={0.5}
              cameraDistance={15}
              disableRotation={false}
            />
          </div>

          <motion.div 
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-center"
            style={{ 
              position: "relative",
              zIndex: 1,
              marginTop: "10vh"
            }}
          >
            <h1 style={{ color: "#2BC6D1", fontSize: "6rem", fontWeight: "bolder", textShadow: "0px 0px 10px rgba(0, 0,0, 0.5)" }}>
              <GradientText
                colors={["#2BC6D1", "#28007B", "#2BC6D1", "#28007B", "#2BC6D1"]}
                animationSpeed={9}
                showBorder={false}
                className="custom-class"
              >
                Welcome
              </GradientText>
            </h1>
            <p style={{ 
              color: "#FFFFFF", 
              fontSize: "1.5rem",
              marginTop: "2rem",
              maxWidth: "800px",
              marginLeft: "auto",
              marginRight: "auto"
            }}>
              Experience the future of interactive communication with our cutting-edge platform.
            </p>
          </motion.div>

          <Row className="mt-5 justify-content-center">
            {notebooks.map((notebook, index) => (
              <Col key={index} md={4} className="mb-4">
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <Card style={{ backgroundColor: "#1A1A1A", color: "#FFFFFF", borderRadius: "15px", cursor: "pointer" }}>
                    <Card.Body>
                      <Card.Title style={{ color: "#2BC6D1" }}>{notebook.title}</Card.Title>
                      <Card.Text>{notebook.description}</Card.Text>
                    </Card.Body>
                  </Card>
                </motion.div>
              </Col>
            ))}
          </Row>
        </Col>
      </Row>
    </Container>
  );
}

export default Home;
